import React, { Component } from 'react'
import StockService from '../services/StockService';

class CreateEmployeeComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
            // step 2
            id: this.props.match.params.id,
            stockSymbol: '',
            price: '',
            trend: ''
        }
        this.changeStockSymbolHandler = this.changeStockSymbolHandler.bind(this);
        this.changePriceHandler = this.changePriceHandler.bind(this);
        this.saveOrUpdateStock = this.saveOrUpdateStock.bind(this);
    }

    // step 3
    componentDidMount(){

        // step 4
        if(this.state.id === '_add'){
            return
        }else{
            StockService.getStockById(this.state.id).then( (res) =>{
                let stock = res.data;
                this.setState({stockSymbol: stock.stockSymbol,
                    price: stock.bidPrice,
                    trend : stock.trend
                });
            });
        }        
    }
    saveOrUpdateStock = (e) => {
        e.preventDefault();
        let Stock = {stockSymbol: this.state.stockSymbol, bidPrice: this.state.bidPrice};
        console.log('stock => ' + JSON.stringify(stock));

        // step 5
        if(this.state.id === '_add'){
            StockService.createStock(stock).then(res =>{
                this.props.history.push('/stock');
            });
        }else{
            StockService.updateStock(employee, this.state.id).then( res => {
                this.props.history.push('/employees');
            });
        }
    }
    
    changeFirstNameHandler= (event) => {
        this.setState({firstName: event.target.value});
    }

    changeLastNameHandler= (event) => {
        this.setState({lastName: event.target.value});
    }

    changeEmailHandler= (event) => {
        this.setState({emailId: event.target.value});
    }

    cancel(){
        this.props.history.push('/employees');
    }

    getTitle(){
        if(this.state.id === '_add'){
            return <h3 className="text-center">Add Employee</h3>
        }else{
            return <h3 className="text-center">Update Employee</h3>
        }
    }
    render() {
        return (
            <div>
                <br></br>
                   <div className = "container">
                        <div className = "row">
                            <div className = "card col-md-6 offset-md-3 offset-md-3">
                                {
                                    this.getTitle()
                                }
                                <div className = "card-body">
                                    <form>
                                        <div className = "form-group">
                                            <label> Stock Symbol: </label>
                                            <input placeholder="Stock Synbol" name="stockSymbol" className="form-control" 
                                                value={this.state.stockSymbol} onChange={this.changeStockSymbolHandler}/>
                                        </div>
                                        <div className = "form-group">
                                            <label> Price: </label>
                                            <input placeholder="Last Name" name="lastName" className="form-control" 
                                                value={this.state.lastName} onChange={this.changeLastNameHandler}/>
                                        </div>
                                        <div className = "form-group">
                                            <label> Trend: </label>
                                            <input placeholder="Email Address" name="emailId" className="form-control" 
                                                value={this.state.emailId} onChange={this.changeEmailHandler}/>
                                        </div>

                                        <button className="btn btn-success" onClick={this.saveOrUpdateEmployee}>Save</button>
                                        <button className="btn btn-danger" onClick={this.cancel.bind(this)} style={{marginLeft: "10px"}}>Cancel</button>
                                    </form>
                                </div>
                            </div>
                        </div>

                   </div>
            </div>
        )
    }
}

export default CreateEmployeeComponent
