import React, { Component } from 'react'
import StockService from '../services/StockService';

class ViewStockComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
            id: this.props.match.params.id,
            stock: {}
        }
    }

    componentDidMount(){
        StockService.getStockById(this.state.id).then( res => {
            this.setState({stock: res.data});
        })
    }

    render() {
        return (
            <div>
                <br></br>
                <div className = "card col-md-6 offset-md-3">
                    <h3 className = "text-center"> View stock Details</h3>
                    <div className = "card-body">
                        <div className = "row">
                            <label> Stock Synbol: </label>
                            <div> { this.state.stock.stockSynbol }</div>
                        </div>
                        <div className = "row">
                            <label> Bid Price: </label>
                            <div> { this.state.stock.bidPrice }</div>
                        </div>
                        <div className = "row">
                            <label> Ask Price: </label>
                            <div> { this.state.stock.askPrice }</div>
                        </div>
                    </div>

                </div>
            </div>
        )
    }
}

export default ViewStockComponent
