import React, { Component } from 'react'
import StockService from '../services/StockService';

class ListStockComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
                stocks: []
        }
   
        this.addStock = this.addStock.bind(this);
    //    this.editStock = this.editStock.bind(this);
    //    this.deleteStock = this.deleteStock.bind(this);
    }
/*
    deleteEmployee(id){
        EmployeeService.deleteEmployee(id).then( res => {
            this.setState({employees: this.state.employees.filter(employee => employee.id !== id)});
        });
    }
    viewEmployee(id){
        this.props.history.push(`/view-employee/${id}`);
    }
    editEmployee(id){
        this.props.history.push(`/add-employee/${id}`);
    }

*/

    componentDidMount(){
        StockService.getViewStocks().then((res) => {
            this.setState({ stocks: res.data});
        });

        StockService.getStocks().then((res) => {
            this.setState({ stocks: res.data});
        });
    }

    addStock(){
        this.props.history.push('/add-stock/_add');
    }

    render() {
        return (
            <div>
                 <h2 className="text-center">DBS Stock List</h2>
                 <div className = "row">
                    <button className="btn btn-primary" onClick={this.addStock}> Add Stock</button>
                 </div>
                 <br></br>
                 <div className = "row">
                        <table className = "table table-striped table-bordered">

                            <thead>
                                <tr>
                                    <th> Stock Symbol</th>
                                    <th> Market Price</th>
                                    <th> Trend</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    this.state.stocks.map(
                                        stock => 
                                        <tr key = {stock.id}>
                                             <td> {stock.stockSymbol} </td>   
                                             <td> {stock.marketPrice}</td>
                                             <td>
                                             if ({stock.upDown}) {
                                                  <div>
                                                      <span className="glyphicon glyphicon-arrow-down" aria-hidden="true"></span>
                                                </div>
                                             } esle {
                                                <div>
                                                <span className="glyphicon glyphicon-arrow-up" aria-hidden="true"></span>
                                                </div>
                                             }
                                            </td>
                                        </tr>
                                    )
                                }
                            </tbody>
                        </table>

                 </div>

            </div>
        )
    }
}

export default ListStockComponent
